mapper.select:
select 	count(*)
from	comments
where	COFFEE_NUM = #{COFFEE_NUM}

select *
from ( select rownum RNUM, NUM, ID, CONTENT, REG_DATE, COFFEE_NUM
	from(
		select NUM, ID, CONTENT, REG_DATE, COFFEE_NUM
		from comments
		where COFFEE_NUM = #{COFFEE_NUM}
		order by REG_DATE desc
		)
	)
where rnum between #{start} and #{end}

mapper.update:
update	comments
set		CONTENT=#{CONTENT}
where	NUM = #{NUM}
