mapper.select:
select * from member 
where user_id = #{id} 
and user_pass = #{pass}

mapper.update:
update member
set USER_PASS = #{newpass}
where USER_ID = #{id}

mapper.select:
select * 
from (select rownum rnum, b.*
		from (select *
		       from member
		     where USER_ID !='admin'
			  
			order by USER_ID
		)b	
)
where rnum >=#{start} and rnum  <= #{end}

sql.if:
and
id name nickname gender
like #{search_word}
